$(function() {


	//SVG Fallback
	if(!Modernizr.svg) {
		$("img[src*='svg']").attr("src", function() {
			return $(this).attr("src").replace(".svg", ".png");
		});
	};

	//E-mail Ajax Send
	//Documentation & Example: https://github.com/agragregra/uniMail
	$("form").submit(function() { //Change
		var th = $(this);
		$.ajax({
			type: "POST",
			url: "mail.php", //Change
			data: th.serialize()
		}).done(function() {
			alert("Thank you!");
			setTimeout(function() {
				// Done Functions
				th.trigger("reset");
			}, 1000);
		});
		return false;
	});

	//Chrome Smooth Scroll
	try {
		$.browserSelector();
		if($("html").hasClass("chrome")) {
			$.smoothScroll();
		}
	} catch(err) {

	};

	$("img, a").on("dragstart", function(event) { event.preventDefault(); });

});

$(document).ready(function () {	 
    

    $('.success-secret-slider').magnificPopup({
		delegate: 'a',
		type: 'image',
		closeOnContentClick: false,
		closeBtnInside: false,
		mainClass: 'mfp-with-zoom mfp-img-mobile',
		image: {
			verticalFit: true,
			titleSrc: function(item) {
				return item.el.attr('title') + ' &middot; <a class="image-source-link" href="'+item.el.attr('data-source')+'" target="_blank">открыть в полном размере</a>';
			}
		},
		gallery: {
			enabled: true
		},
		zoom: {
			enabled: true,
			duration: 300, // don't foget to change the duration also in CSS
			opener: function(element) {
				return element.find('img');
			}
		}
		
	}); 	

    $("form .btn").click(function() {
		var text = $(this).attr("data-value");
		$(".hidden-input").val(text);
	});  
	$('.success-secret-slider').owlCarousel();

	$('.test-slider').owlCarousel();


});

$( ".close" ).click(function() {
	$(this).parent().removeClass("active");
});

		//owl-carousel
	 $('.success-secret-slider').owlCarousel({
	            loop:true,
	            margin:10,
	            nav:true,
	            responsive:{
	                0:{
	                    items:1
	                },
	                600:{
	                    items:3
	                },
	                1000:{
	                    items:5
	                }
	            }
	        })
	 $('.test-slider').owlCarousel({
	        loop:true, //Зацикливаем слайдер
			margin:10, //Отступ от картино если выводите больше 1
			nav:true, //Отключил навигацию			
			smartSpeed:1000, //Время движения слайда
			autoplayTimeout:2000, //Время смены слайда
			responsive:{ //Адаптация в зависимости от разрешения экрана
				0:{
					items:1
				},
				600:{
					items:1
				},
				1000:{
					items:1
				}
			}
		});
  
